package ro.bar.sanymotors.image;

import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.util.Iterator;

import javax.imageio.ImageIO;
import javax.imageio.ImageReadParam;
import javax.imageio.ImageReader;
import javax.imageio.stream.ImageInputStream;

import org.apache.commons.codec.binary.Base64;
import org.apache.commons.io.output.ByteArrayOutputStream;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.test.context.ContextConfiguration;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;

@RunWith(SpringJUnit4ClassRunner.class)
@ContextConfiguration(locations = "classpath:test-sany-motors-config.xml")
public class TestBase64Image {

	@Test
	public void generateSubImageString() throws IOException{
		String base64Content = ",,,";
		base64Content.replaceAll("\\s", "");
		System.out.println(base64Content);
		byte[] imageBytes = Base64.decodeBase64(base64Content);
		ByteArrayInputStream in = new ByteArrayInputStream(imageBytes);
		Iterator<ImageReader> readers = ImageIO.getImageReadersByFormatName("jpg");
		ImageReader reader = (ImageReader) readers.next();
		Object source = in; 
		ImageInputStream iis = ImageIO.createImageInputStream(source); 
		reader.setInput(iis, true);
		ImageReadParam param = reader.getDefaultReadParam();
		BufferedImage image = reader.read(0, param);
		BufferedImage subImage = image.getSubimage(20, 20, 100, 100);
		ByteArrayOutputStream bos = new ByteArrayOutputStream();
		ImageIO.write(subImage, "jpg", bos);
		byte[] newImageBytes = bos.toByteArray();
		String newBase64Content = Base64.encodeBase64String(newImageBytes);
		System.out.println(newBase64Content);
	}
}
